import React, { useContext, useMemo, useState } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import { ShopContext } from '../Context/ShopContext';

export const Checkout = () => {
  const { state } = useLocation();
  const navigate = useNavigate();
  const { all_product, cartItems, getTotalCartAmount } = useContext(ShopContext);
  const [placing, setPlacing] = useState(false);

  // Build a cart line-items list
  const items = useMemo(() => {
    return all_product.filter(p => cartItems[p.id] > 0).map(p => ({
      id: p.id,
      name: p.name,
      qty: cartItems[p.id],
      price: p.new_price,
      image: p.image,
      lineTotal: +(p.new_price * cartItems[p.id]).toFixed(2),
    }));
  }, [all_product, cartItems]);

  const fallbackSummary = useMemo(() => ({
    subtotal: getTotalCartAmount(),
    discount: 0,
    tax: +(getTotalCartAmount() * 0.05).toFixed(2),
    shipping: getTotalCartAmount() > 0 ? 40 : 0,
    total: +(getTotalCartAmount() * 1.05 + (getTotalCartAmount() > 0 ? 40 : 0)).toFixed(2),
    coupon: null,
  }), [getTotalCartAmount]);

  const summary = state?.summary || fallbackSummary;

  const [form, setForm] = useState({
    name: '',
    email: '',
    phone: '',
    address: '',
    city: '',
    zip: '',
    payment: 'cod',
  });

  const onChange = (e) => setForm(prev => ({ ...prev, [e.target.name]: e.target.value }));

  const placeOrder = () => {
    if (!items.length) { alert('Your cart is empty'); return; }
    if (!form.name || !form.address || !form.city || !form.zip) { alert('Please complete shipping details'); return; }
    setPlacing(true);
    // Mock placing order
    setTimeout(() => {
      setPlacing(false);
      alert('Order placed! (mock)');
      navigate('/');
    }, 600);
  };

  return (
    <div style={{ display: 'grid', gridTemplateColumns: '2fr 1fr', gap: 24, padding: 16 }}>
      <div>
        <h2>Checkout</h2>
        <div style={{ display: 'grid', gap: 10, maxWidth: 520 }}>
          <input name="name" value={form.name} onChange={onChange} placeholder="Full name" />
          <input name="email" value={form.email} onChange={onChange} placeholder="Email (optional)" />
          <input name="phone" value={form.phone} onChange={onChange} placeholder="Phone (optional)" />
          <input name="address" value={form.address} onChange={onChange} placeholder="Address" />
          <div style={{ display: 'flex', gap: 10 }}>
            <input name="city" value={form.city} onChange={onChange} placeholder="City" style={{ flex: 1 }} />
            <input name="zip" value={form.zip} onChange={onChange} placeholder="ZIP" style={{ width: 120 }} />
          </div>
          <div>
            <label style={{ marginRight: 10 }}>Payment:</label>
            <select name="payment" value={form.payment} onChange={onChange}>
              <option value="cod">Cash on Delivery</option>
              <option value="card">Card (mock)</option>
              <option value="upi">UPI (mock)</option>
            </select>
          </div>
        </div>

        <h3 style={{ marginTop: 24 }}>Items</h3>
        <div style={{ display: 'grid', gap: 8 }}>
          {items.map(it => (
            <div key={it.id} style={{ display: 'flex', gap: 10, alignItems: 'center', border: '1px solid #eee', borderRadius: 8, padding: 8 }}>
              <img src={it.image} alt={it.name} style={{ width: 56, height: 56, borderRadius: 6, objectFit: 'cover' }} />
              <div style={{ flex: 1 }}>
                <div style={{ fontWeight: 600 }}>{it.name}</div>
                <div style={{ fontSize: 12, color: '#666' }}>Qty: {it.qty}</div>
              </div>
              <div style={{ fontWeight: 600 }}>${it.lineTotal.toFixed(2)}</div>
            </div>
          ))}
          {!items.length && (
            <div style={{ color: '#666' }}>No items in cart.</div>
          )}
        </div>
      </div>

      <div>
        <h3>Order Summary</h3>
        <div style={{ border: '1px solid #eee', borderRadius: 8, padding: 12 }}>
          <div style={{ display: 'flex', justifyContent: 'space-between', marginBottom: 6 }}>
            <span>Subtotal</span>
            <span>${summary.subtotal.toFixed(2)}</span>
          </div>
          <div style={{ display: 'flex', justifyContent: 'space-between', marginBottom: 6 }}>
            <span>Tax</span>
            <span>${summary.tax.toFixed(2)}</span>
          </div>
          <div style={{ display: 'flex', justifyContent: 'space-between', marginBottom: 6 }}>
            <span>Shipping</span>
            <span>{summary.shipping === 0 ? 'Free' : `$${summary.shipping.toFixed(2)}`}</span>
          </div>
          <div style={{ display: 'flex', justifyContent: 'space-between', marginBottom: 6 }}>
            <span>Discount {summary.coupon ? `(${summary.coupon.label})` : ''}</span>
            <span>- ${summary.discount.toFixed(2)}</span>
          </div>
          <hr />
          <div style={{ display: 'flex', justifyContent: 'space-between', fontWeight: 700 }}>
            <span>Total</span>
            <span>${summary.total.toFixed(2)}</span>
          </div>
          <button style={{ marginTop: 12, width: '100%' }} onClick={placeOrder} disabled={placing}>
            {placing ? 'Placing Order…' : 'Place Order'}
          </button>
          <button style={{ marginTop: 8, width: '100%', background: '#f5f5f5', border: '1px solid #ddd' }} onClick={() => navigate('/cart')}>
            Back to Cart
          </button>
        </div>
      </div>
    </div>
  );
};

export default Checkout;
