import { Link } from 'react-router-dom';

import React, { useContext, useEffect, useRef, useState } from "react";
import './Navbar.css'
import logo from '../Assets/logo.png'
import cart_icon from '../Assets/cart_icon.png'
import nav_dropdown from '../Assets/nav_dropdown.png'
import { ShopContext } from '../../Context/ShopContext';
import { WishlistContext } from '../../Context/WishlistContext';

export const Navbar = () => {
  const [menu, setMenu] = useState('shop');
  const { getTotalCartItems } = useContext(ShopContext);
  const { count: wishlistCount } = useContext(WishlistContext);
  const menuRef = useRef();
  const [showTranslate, setShowTranslate] = useState(false);

  const dropdown_toggle = (e) => {
    if (menuRef.current) menuRef.current.classList.toggle('nav-menu-visible');
    e.target.classList.toggle('open');
    setShowTranslate(v => !v);
  };

  // Load Google Translate script once
  useEffect(() => {
    if (document.getElementById('google-translate-script')) return;
    window.googleTranslateElementInit = function () {
      if (window.google && window.google.translate) {
        new window.google.translate.TranslateElement({
          pageLanguage: 'en',
          includedLanguages:
            'en,hi,ta,te,ml,bn,gu,mr,pa,kn,ur,fr,de,es,it,pt,ru,zh-CN,ja,ko,ar',
          layout: window.google.translate.TranslateElement.InlineLayout.SIMPLE,
          autoDisplay: false,
        }, 'google_translate_element');
      }
    };
    const s = document.createElement('script');
    s.id = 'google-translate-script';
    s.src =
      'https://translate.google.com/translate_a/element.js?cb=googleTranslateElementInit';
    s.async = true;
    document.body.appendChild(s);
  }, []);

  // Ensure widget mounts when opening dropdown
  useEffect(() => {
    if (!showTranslate) return;
    const el = document.getElementById('google_translate_element');
    if (window.google && window.google.translate && el && el.childElementCount === 0) {
      try {
        new window.google.translate.TranslateElement({
          pageLanguage: 'en',
          includedLanguages:
            'en,hi,ta,te,ml,bn,gu,mr,pa,kn,ur,fr,de,es,it,pt,ru,zh-CN,ja,ko,ar',
          layout: window.google.translate.TranslateElement.InlineLayout.SIMPLE,
          autoDisplay: false,
        }, 'google_translate_element');
      } catch (e) {}
    }
  }, [showTranslate]);

  return (
    <div className='navbar'>
      <div className='nav-logo'>
        <img src={logo} alt='' />
        <p>SHOPPER</p>
      </div>
      <img className='nav-dropdown' onClick={dropdown_toggle} src={nav_dropdown} alt='' />
      <ul ref={menuRef} className='nav-menu'>
        <li onClick={() => { setMenu('shop') }}>
          <Link style={{ textDecoration: 'none' }} to='/'>Shop</Link>
          {menu == 'shop' ? <hr /> : <></>}
        </li>
        <li onClick={() => { setMenu('mens') }}>
          <Link style={{ textDecoration: 'none' }} to='/mens'>Men</Link>
          {menu == 'mens' ? <hr /> : <></>}
        </li>
        <li onClick={() => { setMenu('womens') }}>
          <Link style={{ textDecoration: 'none' }} to='/womens'>Women</Link>
          {menu == 'womens' ? <hr /> : <></>}
        </li>
        <li onClick={() => { setMenu('kids') }}>
          <Link style={{ textDecoration: 'none' }} to='/kids'>Kids</Link>
          {menu == 'kids' ? <hr /> : <></>}
        </li>
      </ul>
      <div className='nav-login-cart'>
        <div className='nav-icon-group'>
          <button className='nav-icon-btn' title='Translate' onClick={() => setShowTranslate(v => !v)}>
            <svg viewBox='0 0 24 24' width='24' height='24' fill='currentColor'>
              <path d='M12 2a10 10 0 100 20 10 10 0 000-20zm7.93 9h-3.16a15.6 15.6 0 00-1.18-5.02A8.02 8.02 0 0119.93 11zM12 4a12.9 12.9 0 011.77 5H10.2A12.9 12.9 0 0112 4zM4.07 13h3.16c.22 1.76.7 3.49 1.44 5.02A8.02 8.02 0 014.07 13zM7.23 11H4.07a8.02 8.02 0 013.6-5.02A15.6 15.6 0 007.23 11zM12 20a12.9 12.9 0 01-1.8-5h3.57A12.9 12.9 0 0112 20zm3.33-5h3.16a8.02 8.02 0 01-4.6 5.02A15.6 15.6 0 0015.33 15zm-6.66 0a13.8 13.8 0 01-1.18-4h8.02c-.18 1.38-.53 2.73-1.02 4H8.67z' />
            </svg>
          </button>
          {showTranslate && (
            <div className='translate-dropdown' onMouseLeave={() => setShowTranslate(false)}>
              <div id='google_translate_element' />
            </div>
          )}
        </div>
        {localStorage.getItem('auth-token')
          ? <button onClick={() => { localStorage.removeItem('auth-token'); localStorage.removeItem('user-key'); window.location.replace('/') }}>Logout</button>
          : <Link to='/login'><button>Login</button></Link>}
        <Link to='/wishlist' className='nav-icon-btn' style={{ position: 'relative' }}>
          <span className='wishlist-heart'>â™¥</span>
          {wishlistCount > 0 && (
            <span className='nav-badge' style={{ position: 'absolute', top: -6, right: -6, background: '#ff6b6b', color: '#fff', borderRadius: 10, padding: '0 6px', fontSize: 12 }}>
              {wishlistCount}
            </span>
          )}
        </Link>
        <Link to='/cart'><img src={cart_icon} alt='' /></Link>
        <div className='nav-cart-count'>{getTotalCartItems()}</div>
      </div>
    </div>
  );
}
